mapper.select:
AND tt.PARENT_ID is null
AND tt.PARENT_ID = #{parentId,jdbcType=NUMERIC}
AND tt.MAIN_VIEW_FLAG=1 ORDER BY tt.ORDER_CODE ASC

mapper.update:
begin
merge into sys_resource_t tar using ( select #{item.resourceId,jdbcType=VARCHAR} resource_id, #{item.parentId,jdbcType=VARCHAR} PARENT_ID, #{item.methodName,jdbcType=VARCHAR} method_name, #{item.methodPath,jdbcType=VARCHAR} method_path, #{item.remark,jdbcType=VARCHAR} remark, #{item.resourceName,jdbcType=VARCHAR} resource_name, #{item.resourceUrl,jdbcType=VARCHAR} resource_url, #{item.orderCode,jdbcType=INTEGER} order_code, #{item.createdUser,jdbcType=NUMERIC} created_user, SYSDATE created_date, #{item.updateLastUser,jdbcType=NUMERIC} update_last_user, SYSDATE update_last_date, #{item.validFlag,jdbcType=INTEGER} valid_flag from dual ) src on ( tar.resource_id=src.resource_id OR tar.resource_url=src.resource_url ) WHEN MATCHED THEN UPDATE SET tar.method_name = src.method_name, tar.method_path = src.method_path, tar.remark = src.remark, tar.PARENT_ID = src.PARENT_ID, tar.resource_name = src.resource_name, tar.order_code = src.order_code, tar.created_user = src.created_user, tar.created_date = src.created_date, tar.update_last_user = src.update_last_user, tar.update_last_date = src.update_last_date, tar.valid_flag = nvl(src.valid_flag,tar.valid_flag) WHEN NOT MATCHED THEN INSERT (resource_id, PARENT_ID, method_name, method_path, remark, resource_name, resource_url, order_code, created_user, created_date, update_last_user, update_last_date, valid_flag) VALUES (SYS_RESOURCE_S.nextval+1000, src.PARENT_ID, src.method_name, src.method_path, src.remark, src.resource_name, src.resource_url, src.order_code, src.created_user, src.created_date, src.update_last_user, src.update_last_date, 1);
END;

mapper.select:
SELECT t.method_name methodName, t.method_path methodPath, t.remark remark, t.resource_id resourceId, t.PARENT_ID parentId, t.resource_name resourceName, t.resource_url resourceUrl FROM sys_resource_t t WHERE t.valid_flag = 1 AND (1=2
OR ( t.resource_id = #{item.resourceId} )
)
